mapper.select:
select count(1) from paper_info
where 1 = 1

  AND paper_name LIKE CONCAT('%', #{paperName, jdbcType=VARCHAR}, '%')


  AND paper_year = #{paperYear, jdbcType=VARCHAR}


  AND paper_meeting = #{paperMeeting, jdbcType=VARCHAR}


  AND paper_writer = #{paperWriter, jdbcType=VARCHAR}


  AND user_name = #{userName, jdbcType=VARCHAR}

select * from paper_info
where 1 = 1

  AND paper_name LIKE CONCAT('%', #{paperName, jdbcType=VARCHAR}, '%')


  AND paper_year = #{paperYear, jdbcType=VARCHAR}


  AND paper_meeting LIKE CONCAT('%', #{paperMeeting, jdbcType=VARCHAR}, '%')


  AND paper_writer = #{paperWriter, jdbcType=VARCHAR}


  AND user_name = #{userName, jdbcType=VARCHAR}

order by create_time desc
limit #{offset},#{limit}

mapper.insert:

insert into paper_info (id, paper_name, paper_year, 
  paper_meeting, paper_writer, create_time, 
  paper_address, ppt_address, user_name
  )
values (#{id,jdbcType=INTEGER}, #{paperName,jdbcType=VARCHAR}, #{paperYear,jdbcType=VARCHAR}, 
  #{paperMeeting,jdbcType=VARCHAR}, #{paperWriter,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{paperAddress,jdbcType=VARCHAR}, #{pptAddress,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}
  )
